mapper.update:
update emp
username = #{username}, password = #{password}, name = #{name}, gender = #{gender}, image = #{image}, job = #{job}, entrydate = #{entrydate}, dept_id = #{deptId}, update_time = #{updateTime}
where id = #{id}

mapper.select:
select * from emp

    name like concat('%',#{name},'%')
    and gender = #{gender}
    and entrydate between #{begin} and #{end}

order by update_time desc

mapper.insert:
insert into emp(
    username, name, gender, image, job,
    entrydate, dept_id, create_time, update_time
) values (
             #{username},#{name},#{gender},#{image},#{job},
             #{entrydate},#{deptId},#{createTime},#{updateTime}
         )
